// Only needed functions of jQuery here
// by failz0r @ 2014

MinQueryObject = function (htmlObject)
{
	if( htmlObject == null ) return false;

	this.obj = htmlObject;
	var me = this;

	this.on = function (spec, func)
	{
		if( spec == "click" ) me.obj.onclick = func;
	}

	this.is = function (param)
	{
		if( param == ":visible" ) return ( me.obj.style.display == "block" );

		return false;
	}

	this.parent = function ()
	{
		return new MinQueryObject( me.obj.parentNode );
	}

	this.addClass = function (cn)
	{
		me.obj.className = me.obj.className + " " + cn.toString();
	}

	this.removeClass = function (cn)
	{
		me.obj.className = me.obj.className.replace( new RegExp( cn, 'g' ) , '' ).replace( /^\s+|\s+$/g, '' );
	}

	this.show = function ()
	{
		me.obj.style.display = "block";
	}

	this.hide = function ()
	{
		me.obj.style.display = "none";
	}
}

$ = function (id)
{
	// catch all directly accessed HTML Elements
	if( id instanceof HTMLElement ) return new MinQueryObject( id );
	
	// catch document
	if( id == document ) return new MinQueryObject( document );

	// catch body, as it does not necessarily has an id
	if( id == "body" ) return new MinQueryObject( document.body );		

	// ... Otherwise find the corresponding HTML Element with this id
	var obj = document.getElementById( id.substring(1) );
	if( obj != null && typeof obj !== "undefined")
	{
		return new MinQueryObject( obj );
	}

	return null;
}